import React, {Component} from 'react';
import  './LeftBottomBox.module.css';


class LeftBottomBox extends Component{

  constructor(props){
    super(props);

    this.state = {
      cssClass: "leftBottom",
      moved: false
    }
  }

  onLeftBottomClicked = () => {
    let classes = [/*styles.leftBottom*/"leftBottom"];
    debugger;
    let newClass = this.state.moved ? classes.push("moved").join(" ") : classes[0]/*styles.leftBottom*/;
    this.setState((prevState,props) => ({
                                          cssClass: newClass,
                                          moved: !prevState.moved
                                        }));
  }

  render(){
    return (
        <div className={this.state.cssClass} onClick={this.onLeftBottomClicked}/>
    );
  }
}

export default LeftBottomBox;
