import React, {Component} from 'react';
import classes from './LeftBottomBox.module.css';


class LeftBotomBox extends Component{

  constructor(props){
    super(props);

    this.state = {
      styleClass: classes.leftBottom,
      moved: false
    }
  }

  onLeftBottomClicked = () => {
    let cssClasses = [classes.leftBottom];
    let newCssClass = this.state.moved ? cssClasses.push("moved").join(" ") : classes.leftBottom;
    this.setState((prevState,props) => ({
                                          styleClass: newCssClass,
                                          moved: !this.prevState.moved
                                        }));
  }

  render(){
    return (
        <div className={this.state.styleClass} onClick={this.onLeftBottomClicked}/>
    );
  }
}

export default LeftBotomBox;
